mapper.select:
SELECT A.RNUM , A.NO , A.TITLE , A.MODIFY_DATE , A.REF_MEMBER_NO , A.REF_CATEGORY_NO , D.NICKNAME , E.NAME FROM (SELECT ROWNUM RNUM , B.NO , B.TITLE , B.MODIFY_DATE , B.REF_MEMBER_NO , B.REF_CATEGORY_NO FROM(SELECT C.NO , C.TITLE , C.MODIFY_DATE , C.REF_MEMBER_NO , C.REF_CATEGORY_NO FROM TBL_SUGGEST C JOIN TBL_MEMBER D ON(C.REF_MEMBER_NO = D.NO) JOIN TBL_CATEGORY E ON(C.REF_CATEGORY_NO = E.NO)
E.NAME LIKE '%' || #{searchValue} || '%'
D.NICKNAME LIKE '%' || #{searchValue} || '%'
C.TITLE LIKE '%' || #{searchValue} || '%'
AND C.DETELE_YN = 'N'
ORDER BY C.MODIFY_DATE DESC )B
WHERE ROWNUM <= #{endRow}
)A JOIN TBL_MEMBER D ON(A.REF_MEMBER_NO = D.NO) JOIN TBL_CATEGORY E ON(A.REF_CATEGORY_NO = E.NO)
WHERE A.RNUM >= #{startRow}
ORDER BY 1 ASC
