mapper.insert:
DECLARE BEGIN
INSERT INTO TBL_PRJ_FAQ TPF (TPF.NO, TPF.REF_PRJ_NO, TPF.TITLE, TPF.CONTENT, TPF.REGIST_DATE) VALUES (SEQ_TBL_PRJ_FAQ_NO.nextval, #{projectFaq.projectNo}, #{projectFaq.title}, #{projectFaq.content}, SYSDATE);
end;

mapper.update:
DECLARE
  BEGIN

        UPDATE TBL_PRJ_FAQ TPF
           SET TPF.CONTENT = #{projectFaq.content},
               TPF.TITLE = #{projectFaq.title}
         WHERE TPF.NO = ${projectFaq.no};

   END;
