mapper.select:
SELECT PRJ.NO
  FROM TBL_PRJ PRJ
  JOIN TBL_MEMBER TM ON TM.NO = PRJ.REF_MEMBER_NO
 WHERE PRJ.NO = #{projectNo}
   AND TM.ID = #{userId}

SELECT PRJ.NO AS TBL_PRJ_NO, PRJ.TITLE AS TBL_PRJ_TITLE, PRJ.CONTENT AS TBL_PRJ_CONTENT, PRJ.REGIST_DATE AS TBL_PRJ_REGEIST_DATE, PRJ.START_DATE AS TBL_PRJ_START_DATE, PRJ.END_DATE AS TBL_PRJ_END_DATE, PRJ.TARGET_AMOUNT AS TBL_PRJ_TARGET_AMOUNT, PRJ.CURRENT_AMOUNT AS TBL_PRJ_CURRENT_AMOUNT FROM TBL_PRJ PRJ
JOIN TBL_MEMBER TM on PRJ.REF_MEMBER_NO = TM.NO
WHERE PRJ.NO = ${projectNo}
AND TM.ID = #{userId}

mapper.update:
UPDATE TBL_PRJ RPJ
SET RPJ.CONTENT = #{content:CLOB}
WHERE RPJ.NO = ${no}
