mapper.select:
SELECT PRJ.NO
  FROM TBL_PRJ PRJ
 WHERE PRJ.NO = #{projectNo}
   AND PRJ.REF_MEMBER_NO = #{userNo}

SELECT * FROM (SELECT ROWNUM AS RNUM, ST.* FROM (SELECT PRJ.NO AS TBL_PRJ_NO, PRJ.TITLE AS TBL_PRJ_TITLE, PRJ.START_DATE AS TBL_PRJ_START_DATE, PRJ.END_DATE AS TBL_PRJ_END_DATE, PRJ.CURRENT_AMOUNT AS TBL_PRJ_CURRENT_AMOUNT, PRJ.VIEWS AS TBL_PRJ_VIEWS, TC.NAME AS TBL_CATEGORY_NAME, TPF.CHANGE_NAME AS TBL_PRJ_FILE_CHANGE_NAME, TPW.REF_MEMBER_NO AS TBL_PRJ_WISH_REF_MEMBER_NO FROM TBL_PRJ PRJ JOIN TBL_CATEGORY TC ON PRJ.REF_CATEGORY_NO = TC.NO JOIN (SELECT * FROM TBL_PRJ_PROGRESS WHERE NO IN (SELECT MAX(NO) AS NO FROM TBL_PRJ_PROGRESS GROUP BY REF_PRJ_NO) ) TPP ON TPP.REF_PRJ_NO = PRJ.NO LEFT JOIN (SELECT * FROM TBL_PRJ_FILE TPF2 WHERE TPF2.TYPE = 'THUMBNAIL' AND TPF2.DELETE_YN = 'N' ) TPF ON TPF.REF_PRJ_NO = PRJ.NO LEFT JOIN TBL_PRJ_WISH TPW ON TPW.REF_PRJ_NO = PRJ.NO WHERE PRJ.END_DATE >= SYSDATE
AND PRJ.REF_CATEGORY_NO = #{categoryNo}
AND PRJ.TITLE LIKE '%' || '${searchTitle}' || '%'
AND PRJ.START_DATE >= #{startDate}
AND PRJ.END_DATE <= #{endDate}
AND PRJ.VIEWS >= #{views}
ORDER BY PRJ.END_DATE ) ST) WHERE RNUM BETWEEN #{startNo} AND #{endNo}

SELECT COUNT(*) FROM TBL_PRJ PRJ JOIN (SELECT * FROM TBL_PRJ_PROGRESS WHERE NO IN (SELECT MAX(NO) AS NO FROM TBL_PRJ_PROGRESS GROUP BY REF_PRJ_NO) ) TPP2 ON TPP2.REF_PRJ_NO = PRJ.NO JOIN TBL_MEMBER TM ON TM.NO = PRJ.REF_MEMBER_NO WHERE PRJ.REF_MEMBER_NO = #{userNo}
AND TPP2.STATUS = #{listType}
AND PRJ.TITLE LIKE '%' || #{search} || '%'

SELECT * FROM (SELECT ROWNUM AS RNUM, ST.* FROM (SELECT PRJ.NO AS TBL_PRJ_NO, PRJ.TITLE AS TBL_PRJ_TITLE, PRJ.CONTENT AS TBL_PRJ_CONTENT, PRJ.REGIST_DATE AS TBL_PRJ_REGIST_DATE, PRJ.TARGET_AMOUNT AS TBL_PRJ_TARGET_AMOUNT, PRJ.CURRENT_AMOUNT AS TBL_PRJ_CURRENT_AMOUNT, TPP2.REGIST_DATE AS TBL_PRJ_PROGRESS_REGIST_DATE, TPP2.STATUS AS TBL_PRJ_PROGRESS_STATUS, PRJ.START_DATE AS TBL_PRJ_START_DATE, PRJ.END_DATE AS TBL_PRJ_END_DATE, TPP2.CONTENT AS TBL_PRJ_PROGRESS_CONTENT, TM.NAME AS TBL_MEMBER_NAME, TM.PHONE AS TBL_MEMBER_PHONE, TM.ID AS TBL_MEMBER_ID, TM.STATUS AS TBL_MEMBER_STATUS, TC.NAME AS TBL_CATEGORY_NAME FROM TBL_PRJ PRJ JOIN (SELECT * FROM TBL_PRJ_PROGRESS WHERE NO IN (SELECT MAX(NO) AS NO FROM TBL_PRJ_PROGRESS GROUP BY REF_PRJ_NO) ) TPP2 ON TPP2.REF_PRJ_NO = PRJ.NO JOIN TBL_MEMBER TM ON TM.NO = PRJ.REF_MEMBER_NO JOIN TBL_CATEGORY TC ON TC.NO = PRJ.REF_CATEGORY_NO WHERE PRJ.REF_MEMBER_NO = #{userNo}
AND TPP2.STATUS = #{listType}
AND PRJ.TITLE LIKE '%' || #{search} || '%'
ORDER BY PRJ.REGIST_DATE DESC) ST ) WHERE RNUM BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE TBL_PRJ RPJ
   SET RPJ.CONTENT = #{content:CLOB}
 WHERE RPJ.NO = ${no}

mapper.delete:
DECLARE
  BEGIN
        DELETE TBL_PRJ_PROGRESS TPP WHERE TPP.REF_PRJ_NO = #{projectNo};
        DELETE TBL_PRJ_FILE TPF WHERE  TPF.REF_PRJ_NO = #{projectNo};
        DELETE TBL_PRJ_NEW_INFO TPNI WHERE TPNI.REF_PRJ_NO = #{projectNo};
        DELETE TBL_PRJ_FAQ TPF2 WHERE TPF2.REF_PRJ_NO = #{projectNo};

        DELETE TBL_PRODUCT TP WHERE TP.NO IN (SELECT TPPL.REF_PRODUCT_NO
                                                FROM TBL_PRJ_PRODUCT_LIST TPPL
                                               WHERE TPPL.REF_PRJ_NO = #{projectNo});

        DELETE TBL_PRJ_PRODUCT_LIST TPPL2 WHERE TPPL2.REF_PRJ_NO = #{projectNo};
        DELETE TBL_PRJ PRJ WHERE PRJ.NO = #{projectNo};
    END;

mapper.select:
SELECT TPW.REF_MEMBER_NO AS TBL_PRJ_WISH_REF_MEMBER_NO,
       TPW.REF_PRJ_NO AS TBL_PRJ_WISH_REF_PRJ_NO
  FROM TBL_PRJ_WISH TPW


          TPW.REF_MEMBER_NO = #{memberNo}


        AND TPW.REF_PRJ_NO = #{projectNo}

mapper.delete:
DELETE TBL_PRJ_WISH TPW
 WHERE TPW.REF_MEMBER_NO = #{memberNo}
   AND TPW.REF_PRJ_NO = #{projectNo}
