mapper.select:
SELECT TPJ1.NO AS TBL_PRJ_PROGRESS_NO, TPJ1.REGIST_DATE AS TBL_PRJ_PROGRESS_REGIST_DATE, TPJ1.STATUS AS TBL_PRJ_PROGRESS_STATUS, TPJ1.CONTENT AS TBL_PRJ_PROGRESS_CONTENT, TPJ1.REF_PRJ_NO AS TBL_PRJ_PROGRESS_REF_PRJ_NO FROM TBL_PRJ_PROGRESS TPJ1 WHERE TPJ1.NO = (SELECT MAX(TPJ2.NO) FROM TBL_PRJ_PROGRESS TPJ2 WHERE TPJ2.REF_PRJ_NO = #{projectNo}
AND TPJ2.STATUS = '${status}'
GROUP BY TPJ2.REF_PRJ_NO)
