mapper.select:
SELECT TC.NO AS TBL_CATEGORY_NO, TC.NAME AS TBL_CATEGORY_NAME FROM TBL_CATEGORY TC
WHERE SUB_NO IS NULL
WHERE SUB_NO = #{categoryNo}

mapper.update:
UPDATE TBL_PRJ RPJ
   SET RPJ.CONTENT = #{content:CLOB}
 WHERE RPJ.NO = ${no}

UPDATE TBL_PRJ_FAQ TPF
   SET TPF.CONTENT = #{content},
       TPF.TITLE = #{title}
 WHERE TPF.NO = ${no}

UPDATE TBL_PRJ_NEW_INFO TPNI
   SET TPNI.DELETE_YN = 'Y'
 WHERE TPNI.NO = ${projectNewsNo}
